import * as React from 'react';
export function NostoCheckout(props) {
    React.useEffect(() => {
        let cancelled = false;
        import('@nosto/nosto-react').then((m) => {
            if (!cancelled)
                m.useNostoCheckout(props);
        });
        return () => {
            cancelled = true;
        };
    }, [props]);
    return null;
}
